import { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const Wrapper = styled.div`
  width: 85%;
  margin: auto;
  display: flex;
`;
const Box = styled.div``;
const Left = styled.div`
  flex: 3;
  border: 1px solid red;
`;

const Form = styled.form`
  display: flex;
  flex-wrap: wrap;
`;
const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 20px 10px 0px 0px;
  padding: 10px;
`;
const Button = styled.button`
  width: 40%;
  border: none;
  padding: 12px 20px;
  background-color: #2879fe;
  color: white;
  cursor: pointer;
  &:disabled {
    cursor: not-allowed;
    background-color: grey;
  }
`;
const Error = styled.span`
  color: red;
  margin: 8px;
`;
const Text = styled.div`
  margin: 5px 20px;
  font-size: 12px;
  text-decoration: underline;
  cursor: pointer;
`;
const Agreement = styled.span`
  font-size: 14px;
  margin: 20px 0px;
`;
const Right = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  border: 1px solid red;

`;
const Img=styled.img`

`
const Home = () => {
  const [auth, setAuth] = useState(false);
  const handleInput = () => {};
  const handleSubmit = () => {};
  return (
    <Wrapper>
      <Left>
        <h1>Fill the Application</h1>
        <Form>
          <Input
            onChange={handleInput}
            name="first_name"
            placeholder="First Name"
            type="text"
          />
          <Input
            onChange={handleInput}
            name="last_name"
            placeholder="Last Name"
            type="text"
          />
          <Input
            onChange={handleInput}
            name="email"
            placeholder="email"
            type="email"
          />
          <Input
            onChange={handleInput}
            name="password"
            placeholder="password"
            type="password"
          />
          <Input
            onChange={handleInput}
            name="dob"
            placeholder="DOB (date of birth)"
            type="date"
          />
          <Box>
            Choose your picture
            <Input onChange={handleInput} name="dob" type="file" />
          </Box>
          <Agreement>
            By creating an account, I consent to the processing of my personal
            data in accordance with the <b>PRIVACY POLICY</b>
          </Agreement>
          <Button disabled={auth.isLoading} onClick={handleSubmit}>
            CREATE
          </Button>

          <Text>
            <Link to="/signup" className="links">
              ALREADY HAVE A ACCOUNT?
            </Link>
          </Text>
          {auth.isFailure && <Error>{auth.message}</Error>}
        </Form>
      </Left>
      <Right>
        <Img src="https://images.unsplash.com/photo-1550155864-3033f844da36?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MjZ8fGNvdXBsZXxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60" alt="your picture" />
      </Right>
    </Wrapper>
  );
};

export default Home;
